mapper.select:
select u.email_adrs as username, c.rmbr_me_series as series, c.rmbr_me_tkn as tokenValue, c.rmbr_me_latest_used_tm as date from usr_crtfct c
    inner join usr u on c.usr_id = u.usr_id
where c.rmbr_me_series = #{rememberMeSeries}

mapper.update:
update usr_crtfct c inner join usr u on c.usr_id = u.usr_id
set c.rmbr_me_series = null, c.rmbr_me_tkn = null, c.rmbr_me_latest_used_tm = null
where u.email_adrs = #{emailAddress}
